import React,{ useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import ProductCard from './ProductCard';
import Container from './Container';
import SearchPage from './SearchPage';
import { useGlobalContext } from './context';

const Searchbar = () => {
  const {setStoreList,searchQuery, setSearchQuery,setClickSearch} = useGlobalContext();
  // const[storeList, setStoreList] = useState([]);
  
  const handleSearch = async (event) => {
    event.preventDefault();
    try {
      // Handle successful response from backend
      const response =  await  axios.post('http://localhost:4000/api/searchResult', { query: searchQuery });
      console.log(response.data);
      setStoreList(response.data);
    } catch (error) {
      // Handle error response from backend
      console.log(error);
    }
  }
  return (
    <div>
      <form className="d-flex" id ='container'onSubmit={handleSearch}>
        <input 
          name='item' 
          method='post' 
          className="search-box" 
          type="search" placeholder="Search" 
          value={searchQuery} 
          onChange={(event) => setSearchQuery(event.target.value)}
        />
        <button  type="submit" className="search-button" >Search</button>
        {/* <Container class1="featured-wrapper py-5 home-wrapper-2">
            <div className="row">
              <div className="col-12">
                <h3 className="section-heading">Featured Collection</h3>
              </div>
              {
                storeList.length >=1 ? storeList.map((store,idx) => {
                  return <ProductCard name ={ store._source.storeName}/>
                            
                }):''
              }
          </div>
        </Container> */}
      </form>
    </div>
  )
}

export default Searchbar;
