    //       console.log(response.data);
    //       setStoreList(response.data);
    //     } catch (error) {
    //       // Handle error response from backend
    //       console.log(error);
    //     }
    //   }
      useEffect(() =>{
        // if(clickSearch === true){
        //   console.log(clickSearch);
        //   clickSearch = false;  
          
        // }
        getAll();
      },[]);
    return <AppContext.Provider value={ {storeList,setStoreList,clickSearch,searchQuery,setSearchQuery,setClickSearch}}>{children}</AppContext.Provider>  
}

const useGlobalContext = () => {
    return useContext(AppContext);
};
export { AppContext,AppProvider,useGlobalContext};
